mapper.update:
update gh_comment

    active = true
    , body = #{body}
    , parent_id = #{parentId}
    , created_at = #{createdAt}
    , updated_at = #{updatedAt}
    , html_url = #{htmlUrl}
    , url = #{url}

where gh_id = #{ghId};

mapper.select:
select count(gh_id) > 0
from gh_comment
where gh_id = #{ghId}
