mapper.insert:
insert into gh_event ( gh_id, gh_actor_login, event, commit_id, commit_url
                     , url, created_at
                     , issue_sid
                     , repository_sid
                     , label_sid)
values ( #{ghId}, #{ghActorLogin}, #{event}, #{commitId}, #{commitUrl}
       , #{url}, #{createdAt}
       , (select sid from gh_issue where gh_id = #{ghIssueId})
       , #{label.repositorySid}
       , (select sid from gh_label where name = #{label.name} and repository_sid = #{label.repositorySid}))
;

mapper.update:
update gh_event

    , gh_actor_login = #{ghActorLogin}
    , event = #{event}
    , commit_id = #{commitId}
    , commit_url = #{commitUrl}
    , url = #{url}
    , created_at = #{createdAt}

where gh_id = #{ghId};

mapper.select:
select count(gh_id) > 0
from gh_event
where gh_id = #{ghId}

select e.*, c.message as commit_message
from gh_event e left join gh_commit c on e.commit_id = c.sha
where e.repository_sid = #{repositorySid} and issue_sid = #{sid}
