mapper.insert:
insert into gh_issue ( gh_id, issue_number, title, body, state
                     , pull_request, locked
                     , html_url, url
                     , created_at, updated_at, closed_at
                     , repository_sid
                     , label_ids)
values ( #{ghId}, #{issueNumber}, #{title}, #{body}, #{state}
       , #{pullRequest}, #{locked}
       , #{htmlUrl}, #{url}
       , #{createdAt}, #{updatedAt}, #{closedAt}
       , (select sid from gh_repository where gh_id = #{ghRepositoryId})
       , #{labelIds})

mapper.update:
update gh_issue

    , title = #{title}
    , body = #{body}
    , state = #{state}
    , pull_request = #{pullRequest}
    , locked = #{locked}
    , html_url = #{htmlUrl}
    , url = #{url}
    , created_at = #{createdAt}
    , updated_at = #{updatedAt}
    , closed_at = #{closedAt}
    , label_ids = #{labelIds}

where gh_id = ${ghId}

mapper.select:
select count(sid) > 0
from gh_issue


        gh_id = #{ghId}
        issue_number = #{issueNumber}
