mapper.insert:
insert into gh_owner_info ( gh_id
                          , name, email, login, html_url, url
                          , created_at, updated_at)
values ( #{ghId}
       , #{name}, #{email}, #{login}, #{htmlUrl}, #{url}
       , #{createdAt}, #{updatedAt});

mapper.update:
update gh_owner_info

    , name = #{name}
    , email = #{email}
    , login = #{login}
    , html_url = #{htmlUrl}
    , url = #{url}
    , created_at = #{createdAt}
    , updated_at = #{updatedAt}

where gh_id = #{ghId};

mapper.select:
select sid
from gh_owner_info
where gh_id = #{ghId}
