mapper.insert:
insert into gh_repository ( gh_id
                          , name, full_name, description, gh_private
                          , language, html_url, ssh_url, url
                          , created_at, updated_at
                          , owner_sid)
values ( #{ghId}
       , #{name}, #{fullName}, #{description}, #{ghPrivate}
       , #{language}, #{htmlUrl}, #{sshUrl}, #{url}
       , #{createdAt}, #{updatedAt}
       , (select sid from gh_owner_info where gh_id = #{ghOwnerId}));

mapper.update:
update gh_repository

    active = true
    , name = #{name}
    , full_name = #{fullName}
    , description = #{description}
    , gh_private = #{ghPrivate}
    , language = #{language}
    , html_url = #{htmlUrl}
    , ssh_url = #{sshUrl}
    , created_at = #{createdAt}
    , updated_at = #{updatedAt}
    , owner_sid = (select sid from gh_owner_info where gh_id = #{ghOwnerId})

where gh_id = #{ghId};

mapper.select:
select sid
from gh_repository


        gh_id = #{ghId}
        full_name = #{fullName}
        name = #{name}
